import countries from '../../countries.json'
import { useState, useEffect } from 'react'
import { Container, Form, FormControl, Button, Col, Row } from 'react-bootstrap'

const CountriesPage = () => {
    const [input, setInput] = useState({ country: '' })
    const [country, setCountry] = useState()

    useEffect(() => {
        countries?.map(country => {
            if (input.country === country.name) {
                setCountry(country)
            }
        })
    }, [input.country])


    const handleInput = e => {
        const { name, value } = e.target
        setInput({ ...input, [name]: value })
    }

    const handleSubmit = e => {
        e.preventDefault()
        oneCall()
    }

    const oneCall = () => {
        const country = countries.find(country => country.name === input.country)
        setCountry(country)
        console.log('pais', country.currency)
    }



    return (
        // const {name, currency} = country

        < Container className="main" >
            <h1>Countries Page</h1>

            {/* <ul>
                {countries.map(country => (<li key={country.name}>{country.name}</li>))}
            </ul> */}

            <Form className="mb-3 mt-5" onSubmit={handleSubmit}>
                <FormControl

                    id="sourcecountry"
                    type="search"
                    placeholder="Remitente"
                    name='country'
                    aria-label="Search"
                    value={input.country}
                    onChange={handleInput}
                />
                <FormControl

                    id="targetcountry"
                    type="search"
                    placeholder="Destinatario"
                    name='country'
                    aria-label="Search"
                    value={input.country}
                    onChange={handleInput}
                />
                <br />
                <Button variant="primary" type="submit">
                    Submit
                </Button>
            </Form>

            {/* <Container className="country">
                    <Col>
                        <h2>{country?.name}</h2>
                        <p>{country?.currency}</p>

                    </Col>

                </Container> */}


        </Container >

    )
}
export default CountriesPage
