import apiService from '../../services/api.service'
import { useState, useEffect } from 'react'
import { Container, Form, FormControl, Button } from 'react-bootstrap'
// import { useParams } from 'react-router-dom'

const IndexPage = () => {
    const [input, setInput] = useState({
        sourceCurrency: "",
        targetCurrency: "",
        sendAmount: 0
    })

    const [source, setSource] = useState('')

    const { sourceCurrency, targetCurrency, sendAmount } = input

    useEffect(() => {
        oneCall()
    }, [input])

    const handleInput = e => {
        const { name, value } = e.target
        setInput({ ...input, [name]: value })

        // setSource(e.target.value)
    }
    const oneCall = () => {
        apiService
            .getInput(sourceCurrency, targetCurrency, sendAmount)
            .then(({ data }) => {
                setSource(data)
                console.log(data)
            })
            .catch(err => console.log(err))

    }

    const handleSubmit = e => {
        e.preventDefault()
    }


    return (

        <>
            <Container className="input">
                <Form className="d-flex mb-3 mt-5" onSubmit={handleSubmit}>
                    <FormControl
                        id="sourceCurrency"
                        type="text"
                        placeholder="País de origen"
                        name='sourceCurrency'
                        value={sourceCurrency}
                        onChange={handleInput}
                    />
                    <FormControl
                        id="targetCurrency"
                        type="text"
                        placeholder="País de destino"
                        name='targetCurrency'
                        value={targetCurrency}
                        onChange={handleInput}
                    />
                    <FormControl
                        id="sendAmount"
                        type="number"
                        placeholder="Cantidad a enviar"
                        name='sendAmount'
                        value={sendAmount}
                        onChange={handleInput}
                    />
                    <Button className="form-button" type="submit">Buscar</Button>
                </Form >

            </Container >
            <br />
            <br />
            <br />
            <br />
            <br />
            <br />
            <br />
            <br />

            <h1>Indexpage funcional</h1>
            <p>Prueba enviando 1000 euros a méxico</p>
            {
                input.map(provider => {
                    const { name, type, quotes } = provider
                    return <>
                        <p>{type}: {name}</p>
                        <p>tipo de cambio: {quotes[0].rate} mxn</p>
                        <p>comisión: {quotes[0].fee} euros</p>
                        <p>cantidad recibida: {quotes[0].receivedAmount} mxn</p>
                        <hr />
                    </>
                })
            }
        </>
    )

}
export default IndexPage
