    const [input, setInput] = useState({ country: '' })
    const [country, setCountry] = useState()

    useEffect(() => {
        countries?.map(country => {
            if (input.country === country.name) {
                setCountry(country)
            }
        })
    }, [input.country])


    const handleInput = e => {
        const { name, value } = e.target
