import countries from '../../countries.json'
import { useState } from 'react'
import { Container, Form, FormControl, Button, Col, Row } from 'react-bootstrap'

const CountriesPage = () => {
    const [input, setInput] = useState({ country: '' })
    const [country, setCountry] = useState()

    const handleInput = e => {
        const { name, value } = e.target
        setInput({ ...input, [name]: value })
    }

    const handleSubmit = e => {
        e.preventDefault()
        oneCall()
    }

    const oneCall = () => {
        const country = countries.find(country => country.name === input.country)
        setCountry(country)
    }



    return (
        <Container className="main">
            <h1>Countries Page</h1>

            {/* <ul>
                {countries.map(country => (<li key={country.name}>{country.name}</li>))}
            </ul> */}

            <Form className="mb-3 mt-5" onSubmit={handleSubmit}>
                <FormControl

                    id="country"
                    type="search"
                    placeholder="Country"
                    name='country'
                    aria-label="Search"
                    value={input.country}
                    onChange={handleInput}
                />
                <br />
                <Button variant="primary" type="submit">
                    Submit
                </Button>
            </Form>

            <Container className="country">
                <Row>
                    <Col>
                        <h2>{country.name}</h2>
                        <p>{country.capital}</p>
                        <p>{country.region}</p>
                        <p>{country.subregion}</p>

                    </Col>
                    <Col>

                        <img src={country.flag} alt="testing color" />
                    </Col>
                </Row>

            </Container>


        </Container>
    )
}
export default CountriesPage
